import React, { useState } from "react";
import './TicTacToe.css';
import blue from '../assets/blue.svg';
import red from '../assets/red.svg';

const data = ["", "", "", "", "", "", "", "", ""];

const TicTacToe = () => {
    let [count, setCount] = useState(0);
    let [lock, setLock] = useState(false);

    const toggle = (e, num) => {
        if (lock || data[num] !== "") {
            return;
        }

        if (count % 2 === 0) {
            e.target.innerHTML = `<img src='${blue}' alt='X'/>`;
            data[num] = 'x';
        } else {
            e.target.innerHTML = `<img src='${red}' alt='O'/>`;
            data[num] = 'o';
        }

        setCount(count + 1);

        // Check for a winner or a tie and set the lock state if necessary
        // (Add your win/tie detection logic here)
    }

    return (
        <div className="container">
            <h1 className="title">Let's<span> PLAY</span></h1>
            <div className="board">
                <div className="box" onClick={(e) => { toggle(e, 0) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 1) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 2) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 3) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 4) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 5) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 6) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 7) }}></div>
                <div className="box" onClick={(e) => { toggle(e, 8) }}></div>
            </div>
            <button className="reset" onClick={() => {
                // Reset the game
                data.fill("");
                setCount(0);
                setLock(false);
                document.querySelectorAll('.box').forEach(box => box.innerHTML = '');
            }}>RESTART</button>
        </div>
    )
}

export default TicTacToe;
