const TicTacToe = () => {

    let [count, setCount] = useState(0);
    let [lock, setLock] = useState(false);


    const toggle = (e, num) => {
        if(lock){
            return 0;
        }
        if(count%2===0){
            e.target.innerHTML = `<img src='${'./assets/blue.png'}'>`
        }

    }

    return (
        <div className="container">
            <h1 className="title">Let's<span> PLAY</span></h1>
