import React, { useState } from "react";
import './TicTacToe.css';
import blue from '../assets/blue.svg';
import red from '../assets/red.svg';

const TicTacToe = () => {
    const [data, setData] = useState(["", "", "", "", "", "", "", "", ""]);
    const [lock, setLock] = useState(false);
    const [currentPlayer, setCurrentPlayer] = useState("X");

    const handleClick = (index) => {
        if (lock || data[index] !== "") return; // Prevent clicks if game is locked or spot is taken

        // Update the board
        let newData = [...data];
        newData[index] = currentPlayer;
        setData(newData);

        // Check for a winner or a tie
        checkWin(newData);

        // Switch the player
        setCurrentPlayer(currentPlayer === "X" ? "O" : "X");
    };

    const checkWin = (newData) => {
        const winningCombinations = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];

        for (let combination of winningCombinations) {
            const [a, b, c] = combination;
            if (newData[a] && newData[a] === newData[b] && newData[a] === newData[c]) {
                won(newData[a]);
                return;
            }
        }

        // Check for a tie
        if (!newData.includes("")) {
            setLock(true);
        }
    };

    const won = (winner) => {
        setLock(true);
    };

    return (
        <div className="container">
            <h1 className="title">Let's<span> PLAY</span></h1>
            <div className="board">
                {data.map((value, index) => (
                    <div key={index} className="box" onClick={() => handleClick(index)}>
                        {value && <img src={value === "X" ? blue : red} alt={value} />}
                    </div>
                ))}
            </div>
            <button className="reset" onClick={() => {
                setData(["", "", "", "", "", "", "", "", ""]);
                setLock(false);
                setCurrentPlayer("X");
            }}>RESTART</button>
        </div>
    );
}

export default TicTacToe;
